import React from 'react';
import type { Meta, StoryObj } from '@storybook/react';
import { Button, Text, Stack, Group } from '@mantine/core';

const meta: Meta = {
  title: 'Generated/Button Click Counter',
  parameters: { layout: 'centered' },
  tags: ['autodocs'],
};

export default meta;
type Story = StoryObj;

export const ClickCounter: Story = {
  render: () => {
    const [count, setCount] = React.useState(0);

    return (
      <Stack align="center" gap="md">
        <Text size="xl" fw={700}>
          Click Count: {count}
        </Text>
        <Button onClick={() => setCount(count + 1)} size="lg">
          Click Me!
        </Button>
        <Button 
          onClick={() => setCount(0)} 
          variant="outline" 
          size="sm"
          disabled={count === 0}
        >
          Reset Counter
        </Button>
      </Stack>
    );
  },
};

export const MultipleCounters: Story = {
  render: () => {
    const [primaryCount, setPrimaryCount] = React.useState(0);
    const [secondaryCount, setSecondaryCount] = React.useState(0);

    return (
      <Stack align="center" gap="xl">
        <Stack align="center" gap="sm">
          <Text size="lg" fw={600}>
            Primary Counter: {primaryCount}
          </Text>
          <Button onClick={() => setPrimaryCount(primaryCount + 1)}>
            Primary Button
          </Button>
        </Stack>

        <Stack align="center" gap="sm">
          <Text size="lg" fw={600}>
            Secondary Counter: {secondaryCount}
          </Text>
          <Button 
            onClick={() => setSecondaryCount(secondaryCount + 1)}
            variant="outline"
          >
            Secondary Button
          </Button>
        </Stack>

        <Text size="sm" c="dimmed">
          Total Clicks: {primaryCount + secondaryCount}
        </Text>
      </Stack>
    );
  },
};

export const IncrementDecrement: Story = {
  render: () => {
    const [count, setCount] = React.useState(0);

    return (
      <Stack align="center" gap="md">
        <Text size="xl" fw={700}>
          Counter: {count}
        </Text>
        <Group gap="md">
          <Button 
            onClick={() => setCount(count - 1)} 
            variant="outline"
            color="red"
          >
            Decrement
          </Button>
          <Button 
            onClick={() => setCount(0)} 
            variant="light"
          >
            Reset
          </Button>
          <Button 
            onClick={() => setCount(count + 1)}
            color="green"
          >
            Increment
          </Button>
        </Group>
      </Stack>
    );
  },
};
