import React from 'react';
import type { Meta, StoryObj } from '@storybook/react';
import { Checkbox, Stack } from '@mantine/core';

const meta: Meta<typeof Checkbox> = {
  title: 'Generated/Checkbox',
  component: Checkbox,
  parameters: { layout: 'centered' },
  tags: ['autodocs'],
  argTypes: {
    label: { control: 'text' },
    disabled: { control: 'boolean' },
    error: { control: 'text' },
    description: { control: 'text' },
  },
};

export default meta;
type Story = StoryObj<typeof meta>;

export const Default: Story = {
  args: {
    label: 'I agree to terms and conditions',
  },
};

export const Checked: Story = {
  args: {
    label: 'Subscribe to newsletter',
    defaultChecked: true,
  },
};

export const WithDescription: Story = {
  args: {
    label: 'Enable notifications',
    description: 'You will receive email notifications for important updates',
  },
};

export const Disabled: Story = {
  args: {
    label: 'This option is disabled',
    disabled: true,
  },
};

export const WithError: Story = {
  args: {
    label: 'Accept privacy policy',
    error: 'You must accept the privacy policy to continue',
  },
};

export const MultipleCheckboxes: Story = {
  render: () => (
    <Stack gap="md">
      <Checkbox label="Receive email notifications" />
      <Checkbox label="Receive SMS notifications" />
      <Checkbox label="Receive push notifications" defaultChecked />
      <Checkbox label="Weekly newsletter" />
    </Stack>
  ),
};
