import React from 'react';
import type { Meta, StoryObj } from '@storybook/react';
import { Modal, Button, Text, TextInput, Group, Stack } from '@mantine/core';

const meta: Meta = {
  title: 'Generated/Modal Variations',
  id: 'modal-variations-9c4a213d',
  parameters: { layout: 'centered' },
  tags: ['autodocs'],
};

export default meta;
type Story = StoryObj;

export const SimpleModal: Story = {
  render: () => {
    const [opened, setOpened] = React.useState(false);

    return (
      <>
        <Button onClick={() => setOpened(true)}>Open Simple Modal</Button>
        <Modal
          opened={opened}
          onClose={() => setOpened(false)}
          title="Simple Modal"
        >
          <Text>
            This is a simple modal with some text content. You can add any
            content you want here.
          </Text>
          <Group mt="md">
            <Button onClick={() => setOpened(false)}>Close</Button>
          </Group>
        </Modal>
      </>
    );
  },
};

export const ConfirmationModal: Story = {
  render: () => {
    const [opened, setOpened] = React.useState(false);

    return (
      <>
        <Button onClick={() => setOpened(true)} color="red">
          Delete Item
        </Button>
        <Modal
          opened={opened}
          onClose={() => setOpened(false)}
          title="Confirm Deletion"
          centered
        >
          <Stack gap="md">
            <Text>
              Are you sure you want to delete this item? This action cannot be
              undone.
            </Text>
            <Group justify="flex-end">
              <Button variant="default" onClick={() => setOpened(false)}>
                Cancel
              </Button>
              <Button color="red" onClick={() => setOpened(false)}>
                Delete
              </Button>
            </Group>
          </Stack>
        </Modal>
      </>
    );
  },
};

export const FormModal: Story = {
  render: () => {
    const [opened, setOpened] = React.useState(false);

    return (
      <>
        <Button onClick={() => setOpened(true)}>Open Form Modal</Button>
        <Modal
          opened={opened}
          onClose={() => setOpened(false)}
          title="Create New User"
          size="lg"
        >
          <Stack gap="md">
            <TextInput
              label="Full Name"
              placeholder="Enter your full name"
              required
            />
            <TextInput
              label="Email"
              placeholder="your@email.com"
              type="email"
              required
            />
            <TextInput
              label="Phone Number"
              placeholder="+1 (555) 000-0000"
            />
            <Group justify="flex-end" mt="md">
              <Button variant="default" onClick={() => setOpened(false)}>
                Cancel
              </Button>
              <Button onClick={() => setOpened(false)}>
                Create User
              </Button>
            </Group>
          </Stack>
        </Modal>
      </>
    );
  },
};

