import React from 'react';
import type { Meta, StoryObj } from '@storybook/react';
import { Badge, Avatar, Stack, Group, Button, Paper, Text } from '@mantine/core';

const meta: Meta<typeof Badge> = {
  title: 'Generated/Notification Badge',
  component: Badge,
  parameters: { layout: 'centered' },
  tags: ['autodocs'],
  argTypes: {
    variant: {
      control: 'select',
      options: ['filled', 'light', 'outline', 'dot'],
    },
    color: {
      control: 'select',
      options: ['blue', 'red', 'green', 'yellow', 'gray'],
    },
    size: {
      control: 'select',
      options: ['xs', 'sm', 'md', 'lg', 'xl'],
    },
  },
};

export default meta;
type Story = StoryObj<typeof meta>;

export const Default: Story = {
  args: {
    children: '3',
    color: 'red',
    variant: 'filled',
    size: 'md',
  },
};

export const DotVariant: Story = {
  args: {
    variant: 'dot',
    color: 'red',
  },
};

