import React from 'react';
import type { Meta, StoryObj } from '@storybook/react';
import { Radio, RadioGroup, Stack } from '@mantine/core';

const meta: Meta<typeof Radio> = {
  title: 'Generated/Radio Button',
  component: Radio,
  parameters: { layout: 'centered' },
  tags: ['autodocs'],
};

export default meta;
type Story = StoryObj<typeof meta>;

export const Default: Story = {
  args: {
    label: 'Default radio',
    value: 'default',
  },
};

export const Checked: Story = {
  args: {
    label: 'Checked radio',
    value: 'checked',
    defaultChecked: true,
  },
};

export const Disabled: Story = {
  args: {
    label: 'Disabled radio',
    value: 'disabled',
    disabled: true,
  },
};

export const WithDescription: Story = {
  args: {
    label: 'Radio with description',
    description: 'This is a helpful description for the radio button',
    value: 'description',
  },
};

export const RadioGroupExample: Story = {
  render: () => (
    <RadioGroup
      label="Select your favorite framework"
      description="Choose the one you like most"
      defaultValue="react"
    >
      <Stack mt="xs">
        <Radio value="react" label="React" />
        <Radio value="vue" label="Vue" />
        <Radio value="angular" label="Angular" />
        <Radio value="svelte" label="Svelte" />
      </Stack>
    </RadioGroup>
  ),
};

export const RadioGroupHorizontal: Story = {
  render: () => (
    <RadioGroup
      label="Size"
      defaultValue="medium"
    >
      <Stack mt="xs">
        <Radio value="small" label="Small" />
        <Radio value="medium" label="Medium" />
        <Radio value="large" label="Large" />
      </Stack>
    </RadioGroup>
  ),
};
