import React from 'react';
import type { Meta, StoryObj } from '@storybook/react';
import { Stepper, TextInput, PasswordInput, Progress, Button, Group, Stack, Paper, Text, Title } from '@mantine/core';

const meta: Meta = {
  title: 'Generated/Signup Wizard with Password Strength',
  id: 'signup-wizard-with-password-strength-d0239b03',
  parameters: { layout: 'centered' },
  tags: ['autodocs'],
};

export default meta;
type Story = StoryObj;

export const SignupWizard: Story = {
  render: () => {
    const [active, setActive] = React.useState(0);
    const [name, setName] = React.useState('');
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');

    const calculatePasswordStrength = (pass: string): number => {
      let strength = 0;
      if (pass.length >= 8) strength += 25;
      if (pass.length >= 12) strength += 25;
      if (/[A-Z]/.test(pass)) strength += 25;
      if (/[0-9]/.test(pass) && /[^A-Za-z0-9]/.test(pass)) strength += 25;
      return strength;
    };

    const passwordStrength = calculatePasswordStrength(password);

    const getPasswordColor = (strength: number): string => {
      if (strength <= 25) return 'red';
      if (strength <= 50) return 'orange';
      if (strength <= 75) return 'yellow';
      return 'green';
    };

    const nextStep = () => setActive((current) => (current < 3 ? current + 1 : current));
    const prevStep = () => setActive((current) => (current > 0 ? current - 1 : current));

    return (
      <Paper style={{ width: 600, padding: '40px' }} shadow="md">
        <Title order={2} style={{ marginBottom: '30px', textAlign: 'center' }}>
          Create Your Account
        </Title>

        <Stepper active={active} onStepClick={setActive} style={{ marginBottom: '30px' }}>
          <Stepper.Step label="Personal Info" description="Basic details">
            <Stack gap="md" style={{ marginTop: '30px' }}>
              <TextInput
                label="Full Name"
                placeholder="Enter your full name"
                value={name}
                onChange={(e) => setName(e.currentTarget.value)}
                required
              />
              <TextInput
                label="Email Address"
                placeholder="your.email@example.com"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.currentTarget.value)}
                required
              />
            </Stack>
          </Stepper.Step>

          <Stepper.Step label="Security" description="Set password">
            <Stack gap="md" style={{ marginTop: '30px' }}>
              <PasswordInput
                label="Password"
                placeholder="Create a strong password"
                value={password}
                onChange={(e) => setPassword(e.currentTarget.value)}
                required
              />
              <div>
                <Text size="sm" style={{ marginBottom: '8px' }}>
                  Password Strength: {passwordStrength <= 25 ? 'Weak' : passwordStrength <= 50 ? 'Fair' : passwordStrength <= 75 ? 'Good' : 'Strong'}
                </Text>
                <Progress 
                  value={passwordStrength} 
                  color={getPasswordColor(passwordStrength)}
                  size="md"
                />
              </div>
            </Stack>
          </Stepper.Step>

          <Stepper.Step label="Review" description="Confirm details">
            <Stack gap="lg" style={{ marginTop: '30px' }}>
              <Paper p="md" style={{ backgroundColor: '#f8f9fa' }}>
                <Text fw={600} size="sm" style={{ marginBottom: '12px' }}>
                  Personal Information
                </Text>
                <Text size="sm">
                  <strong>Name:</strong> {name || 'Not provided'}
                </Text>
                <Text size="sm">
                  <strong>Email:</strong> {email || 'Not provided'}
                </Text>
                <Button 
                  variant="subtle" 
                  size="xs" 
                  style={{ marginTop: '8px' }}
                  onClick={() => setActive(0)}
                >
                  Edit
                </Button>
              </Paper>

              <Paper p="md" style={{ backgroundColor: '#f8f9fa' }}>
                <Text fw={600} size="sm" style={{ marginBottom: '12px' }}>
                  Security
                </Text>
                <Text size="sm">
                  <strong>Password:</strong> {'â€¢'.repeat(password.length) || 'Not set'}
                </Text>
                <Text size="sm">
                  <strong>Strength:</strong> {passwordStrength <= 25 ? 'Weak' : passwordStrength <= 50 ? 'Fair' : passwordStrength <= 75 ? 'Good' : 'Strong'}
                </Text>
                <Button 
                  variant="subtle" 
                  size="xs" 
                  style={{ marginTop: '8px' }}
                  onClick={() => setActive(1)}
                >
                  Edit
                </Button>
              </Paper>
            </Stack>
          </Stepper.Step>

          <Stepper.Completed>
            <Stack align="center" gap="md" style={{ marginTop: '30px', textAlign: 'center' }}>
              <Title order={3}>Account Created Successfully!</Title>
              <Text>Welcome aboard! Your account has been created.</Text>
              <Button onClick={() => setActive(0)} style={{ marginTop: '20px' }}>
                Start Over
              </Button>
            </Stack>
          </Stepper.Completed>
        </Stepper>

        <Group justify="space-between" style={{ marginTop: '30px' }}>
          <Button 
            variant="default" 
            onClick={prevStep}
            disabled={active === 0}
          >
            Back
          </Button>
          {active < 2 ? (
            <Button onClick={nextStep}>
              Next
            </Button>
          ) : active === 2 ? (
            <Button onClick={nextStep}>
              Submit
            </Button>
          ) : null}
        </Group>
      </Paper>
    );
  },
};
